mapper.select:
SELECT PRODUCT.prod_no, PRODUCT.prod_name, PRODUCT.prod_detail, PRODUCT.manufacture_day, PRODUCT.price, PRODUCT.image_file, PRODUCT.reg_date, PRODUCT.amount, PRODUCT.order_count, TRANSACTION.tran_status_code 
FROM product, transaction
WHERE PRODUCT.prod_no = TRANSACTION.prod_no(+) AND PRODUCT.prod_no = #{prodNo}

mapper.update:
UPDATE PRODUCT

	prod_name = #{prodName:VARCHAR} ,
	prod_detail = #{prodDetail:VARCHAR},
	manufacture_day = #{manuDate:DATE},
	price = #{price:INTEGER},
	image_file = #{fileName:VARCHAR},
	amount = #{amount:INTEGER},
	order_count = #{orderCount:INTEGER}
	
	, star = #{star:INTEGER}
	

WHERE prod_no = #{prodNo}
